                    setorder(true);
                    setaccount(false)
                    setvendor(false)
                  }}><span className="me-2"><i className="bi bi-box2"></i></span>Order</Nav.Link>

                  {data && role === 3 ? <Nav.Link onClick={()=>{
                    setorder(false);
